# question 2 : combien d'handle contiennent un numéro

n = journalists.size
total_integer_present = 0
i = 0

while i <= n
	if journalists[i] == nil
		total_integer_present = total_integer_present
	else  
	test = journalists[i]
	a = test.split(//).map {|x| x[/\d+/]}.compact.size
		if a  >= 1
			total_integer_present = total_integer_present + 1
		else total_integer_present = total_integer_present
		end
	end
	i = i + 1
end

puts "il y a #{total_integer_present} handle qui contiennent au moins un chiffre"

		
# question 3 : Combien d'handle contiennent les 4 lettres du prénom "Aude" à la suite (sans prendre en compte les majuscules) ?

n = journalists.size
total_aude_present = 0
i = 0

while i <= n
	test = journalists[i]
	if /[Aa]ude/.match(test).to_s == "Aude"
			total_aude_present = total_aude_present + 1
	end
	if /[Aa]ude/.match(test).to_s == "aude"
			total_aude_present = total_aude_present + 1
	end
	i = i + 1
end

puts "il y a #{total_aude_present} handle contenant les 4 lettres aude à la suite"


# question 4 : Combien commencent par une majuscule (première lettre juste après le @) ?

